(58, 1, '2024-10-11 08:57:41', '2024-10-11 08:57:41', '{\n    \"widget_block[5]\": {\n        \"value\": {\n            \"raw_instance\": {\n                \"content\": \"<!-- wp:group -->\\n<div class=\\\"wp-block-group\\\"><!-- wp:heading {\\\"style\\\":{\\\"typography\\\":{\\\"fontSize\\\":\\\"50px\\\"}}} -->\\n<h2 class=\\\"wp-block-heading\\\" style=\\\"font-size:50px\\\">Archives</h2>\\n<!-- /wp:heading -->\\n\\n<!-- wp:archives {\\\"style\\\":{\\\"typography\\\":{\\\"fontSize\\\":\\\"15px\\\"}}} /--></div>\\n<!-- /wp:group -->\"\n            }\n        },\n        \"type\": \"option\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-10-11 08:56:47\"\n    }\n}', '', '', 'trash', 'closed', 'closed', '', 'd7d065da-4a2a-4c28-9bc2-afa4a0a576c0', '', '', '2024-10-11 08:57:41', '2024-10-11 08:57:41', '', 0, 'http://wordpress.local/?p=58', 0, 'customize_changeset', '', 0);
