(120, 1, '2024-11-01 09:52:09', '2024-11-01 09:52:09', '#footer h3{\n	color: white;\n	padding-left: 50px;\n	margin-bottom: 25px;\n	font-size: 30px\n}\n.search-form .search-submit {\n  background-color: #28a745;\n  border-radius: 5px;\n	text-transform: capitalize;\n	\n}\n.search-container{\n	display: flex;\n	width: 100%;\n	border: 1px solid gray;\n	padding: 15px;\n	padding-bottom: 10px;\n	border-radius: 5px\n}\n.search-container i{\n	font-size: 20px;\n	justify-content: center;\n	margin: auto;\n	padding-left: 10px;\n	margin-bottom\n}\n.search-container .search-field{\n	border: none;\n}\n.search-form{\n	margin-bottom: 10px\n}\n.wp-block-latest-posts li{\n	display: flex;\n}\n.wp-block-latest-posts__post-date {\n    display: flex;\n    align-items: center;\n    justify-content: space-between;\n    font-size: 16px; /* Kích thước chữ cho ngày tháng */\n    position: relative;\n}\n\n.wp-block-latest-posts__post-date .day-month {\n    display: inline-block;\n    font-size: 20px; /* Kích thước lớn hơn cho ngày và tháng */\n    font-weight: bold;\n    line-height: 1;\n    text-align: center;\n	color: white !important;\n}\n\n.wp-block-latest-posts__post-date .day {\n    display: block;\n    font-size: 15px; /* Kích thước lớn hơn cho ngày */\n    margin-bottom: -4px; /* Điều chỉnh khoảng cách để tạo hiệu ứng phân số */\n	border-bottom: 1px solid gray;\n	padding-bottom: 5px;\n		font-weight: 100 !important;\n}\n\n.wp-block-latest-posts__post-date .month {\n    display: block;\n    font-size: 15px; /* Kích thước nhỏ hơn cho tháng */\n	padding-top: 5px;\n		font-weight: 100 !important;\n}\n\n.wp-block-latest-posts__post-date .year {\n    font-size: 15px; /* Kích thước nhỏ hơn cho năm */\n    color: #666; /* Màu chữ cho năm */\n    margin-left: 5px; /* Khoảng cách giữa phân số và năm */\n	font-weight: bold;\n	color: white !important;\n		font-weight: 100 !important;\n}\n.wp-block-latest-posts__post-title{\n	margin: 8px 0px 0px 50px;\n	color: white !important;\n	font-size: 20px;\n	font-weight: 100 !important;\n}\n.wp-block-group{\n	background-color: #3cc5d8;\n	color: white !important;\n	padding:0px 0px 10px 10px;\n	border-radius: 5px;\n}\n\n\n', 'twentytwenty', '', 'inherit', 'closed', 'closed', '', '73-revision-v1', '', '', '2024-11-01 09:52:09', '2024-11-01 09:52:09', '', 73, 'http://wordpress.local/?p=120', 0, 'revision', '', 0);
